window.onscroll=function(){const s=document.querySelector("#header-sticky"),e=document.querySelector(".peminjaman"),c=document.querySelector(".bg-sticky"),t=document.querySelector(".p");window.scrollY>60?(c.classList.add("bgg"),t.classList.add("opacity-0")):(c.classList.remove("bgg"),t.classList.remove("opacity-0")),window.scrollY>80?(s.classList.add("nav-sticky"),e.classList.remove("opacity-0")):(s.classList.remove("nav-sticky"),e.classList.add("opacity-0"))};
